package Assignement4;
//2. Writa a java program to convert given string in lower and uppercase
public class ToUpperAndLowerCases {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String  s1="roshan";
		String  s2="Roshan";
		{
			System.out.println("To Uppercases: "+s1.toUpperCase());
			System.out.println("To Lowercases: "+s2.toLowerCase());
		}
		
				

	}

}
